//realizar un algoritmo que recorra los numeros del 1 al 10 y muestre la suma de ellos
public class for2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int suma; 
		suma = 0;
	
		for (int i = 1; i<11; i++) {

			suma += i;}
		System.out.println("suma = " + suma);
		

	}

}
