//realizar un algoritmo que lea un numero y muestre la tabla de multiplicar 
public class sucuencialmultiplicar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n, r;
		n = 5;
		
		for(int i = 1; i<100; i++) {
			r = n*i;
			System.out.println(n + "*" + i + "=" + r);
			
		}

	}

}
